import { Injectable } from "@angular/core";

import { Recipe } from "./recipe.model";
import { Ingredient } from "../shared/ingredient.model";
import { ShoppingListService } from "../shopping-list/shopping-list.service";
import { Subject } from "rxjs";

@Injectable()
export class RecipesService {
  recipeChanged = new Subject<Recipe[]>();

  private recipes: Recipe[] = [
    {
      name: "Jellof Rice",
      description: "The best kind of rice ever!!",
      imagePath:
        "https://cdn-image.myrecipes.com/sites/default/files/styles/4_3_horizontal_-_1200x900/public/mrtrending0475.jpg?itok=ULkGk3Pn",
      ingredients: [
        new Ingredient("rice", 1),
        new Ingredient("tomato paste", 10),
        new Ingredient("maggi", 3),
        new Ingredient("groundnut oil", 3)
      ]
    },
    {
      name: "Chicken Stew",
      description: "Amazing stew made with Chiken :)",
      imagePath:
        "data:image/jpeg;base64,",
      ingredients: [
        new Ingredient("Chicken", 1),
        new Ingredient("Tomato paste", 20),
        new Ingredient("Onion", 5),
        new Ingredient("maggi", 3),
        new Ingredient("groundnut oil", 3)
      ]
    },
    {
      name: "Burger",
      description: "Junk but still cool!",
      imagePath:
        "data:image/jpeg;base64,",
      ingredients: [
        new Ingredient("buns", 15),
        new Ingredient("butter", 2),
        new Ingredient("Sugar", 5)
      ]
    },
    {
      name: "Engusi Soup",
      description: "The best Igbo traditonal soup",
      imagePath:
        "data:image/jpeg;base64,",
      ingredients: [
        new Ingredient("Egusi", 4),
        new Ingredient("Red Oil", 4),
        new Ingredient("Crayfish", 5),
        new Ingredient("Vegetable", 3)
      ]
    }
  ];

  constructor(private shoppingListService: ShoppingListService) {}

  getRecipes(): Recipe[] {
    return this.recipes.slice();
  }

  getRecipe(index: number) {
    return this.recipes[index];
  }

  addIngredientsToShoppingList(ingredients: Ingredient[]) {
    this.shoppingListService.addIngredients(ingredients);
  }

  addRecipe(recipe: Recipe) {
    this.recipes.push(recipe);
    this.recipeChanged.next(this.recipes.slice());
  }

  updateRecipe(index: number, newRecipe: Recipe) {
    this.recipes[index] = newRecipe;
    this.recipeChanged.next(this.recipes.slice());
  }

  deleteRecipe(index: number) {
    this.recipes.splice(index, 1);
    this.recipeChanged.next(this.recipes.slice());
  }
}
